import React, { useRef, useState } from 'react';
import { useParams } from 'react-router-dom';
import { Col, Container, Form, ListGroup, Row } from 'reactstrap';
import tourData from "../assets/data/tours";
import avatar from '../assets/images/avatar.jpg';
import BookingForm from '../components/BookingForm';
import "../styles/tour-details.css";
import calculateavgRating from '../utils/avgRating';

const TourDetails = () => {
  const { id } = useParams();
  const reviewmsgRef = useRef('');
  const [tourRating, setTourRating] = useState(null);

  const tour = tourData.find(tour => tour.id === id);
  const { photo, title, desc, price, reviews, address, city, distance, maxGroupSize } = tour;

  const { totalRating, avgRating } = calculateavgRating(reviews);
  const options = { day: 'numeric', month: 'long', year: 'numeric' };
  const submitHandler = (e) => {
    e.preventDefault();
    const reviewtext = reviewmsgRef.current.value;
  };

  return (
    <>
      <section>
        <Container>
          <Row>
            <Col lg="8">
              <div className="tour__content">
                <img src={photo} alt='' />
                <div className="tour__info">
                  <h2>{title}</h2>
                  <div className='d-flex align-items-center gap-5'>
                    <span className='tour__Rating d-flex align-items-center gap-1'>
                      <i className='ri-star-fill' style={{ color: 'var(--secondary-color)' }} />
                      {avgRating === 0 ? null : avgRating}
                      {totalRating === 0 ? 'Not rated' : <span>({reviews?.length})</span>}
                    </span>
                    <span>
                      <i className='ri-map-pin-user-fill'></i>{address}
                    </span>
                  </div>
                  <div className="extra_tour_details">
                    <span><i className='ri-map-pin-2-line'></i>{city}</span>
                    <span>
                      <i className='ri-money-dollar-circle-line'></i>${price}/Person
                    </span>
                    <span>
                      <i className='ri-map-pin-time-line'></i>{distance} k/m
                    </span>
                    <span><i className='ri-group-line'></i>{maxGroupSize} People</span>
                  </div>
                  <h5>Description</h5>
                  <p>{desc}</p>
                </div>
                {/* tour review  */}
                <div className="tour__reviews mt-4">
                  <h4>
                    Reviews({reviews?.length} Reviews)
                  </h4>
                  <Form onSubmit={submitHandler}>
                    <div className='d-flex align-items-center gap-3 mb-4 rating__group'>
                      <span onClick={() => setTourRating(1)}>1<i className='ri-star-s-fill'></i></span>
                      <span onClick={() => setTourRating(2)}>2<i className='ri-star-s-fill'></i></span>
                      <span onClick={() => setTourRating(3)}>3<i className='ri-star-s-fill'></i></span>
                      <span onClick={() => setTourRating(4)}>4<i className='ri-star-s-fill'></i></span>
                      <span onClick={() => setTourRating(5)}>5<i className='ri-star-s-fill'></i></span>
                    </div>
                    <div className='review__input'>
                      <input type="text" ref={reviewmsgRef} placeholder='share your thoughts' required />
                      <button className="btn primary__btn text-white" type='submit'>
                        submit
                      </button>
                    </div>
                  </Form>
                  <ListGroup className='user__reviews'>
                    {reviews?.map(review => (
                      <div className='review__item' key={review.id}>
                        <img src={avatar} alt='' />
                        <div className="w-100">
                          <div className='d-flex align-items-center justify-content-between'>
                            <div>
                              <h5>{review.user}</h5>
                              <p>
                                {new Date(review.date).toLocaleDateString('en-us', options)}
                              </p>
                            </div>
                            <span className='d-flex align-items-center'>
                              {review.rating} <i className='ri-star-s-fill'></i>
                            </span>
                          </div>
                          <h6>{review.comment}</h6>
                        </div>
                      </div>
                    ))}
                  </ListGroup>
                </div>
                {/* end section  */}
              </div>
            </Col>
            <Col lg="4">
              <Booking tour={tour} avgRating={avgRating}/>
            </Col>
          </Row>
        </Container>
      </section>
    </>
  );
};

export default TourDetails;
