import React from 'react';
import { Col } from 'reactstrap';
import useFetch from '../../hooks/useFetch';
import TourCard from '../../shared/TourCard'; // Corrected the filename
import { BASE_URL } from '../../utils/config';

function FeaturedTourList() {
    const { data: featuredTours, error, isLoading } = useFetch(
        `${BASE_URL}/search/getFeaturedTours`
    );

    // Handle loading state
    if (isLoading) {
        return <div>Loading...</div>;
    }

    // Handle error state
    if (error) {
        return <div>Error: {error.message}</div>;
    }

    // Handle case where featuredTours is not yet fetched
    if (!featuredTours) {
        return null; // Or any other appropriate action
    }

    return (
        <>
            {featuredTours.map(tour => (
                <Col lg='3' className='mb-5' key={tour._id}>
                    <TourCard tour={tour} />
                </Col>
            ))}
        </>
    );
}

export default FeaturedTourList;
